function init(){fetchCats()}function enable(){$("#add-cat-form").on("submit",processForm)}function processForm(a){a.preventDefault();var b={};$.each($("#add-cat-form").serializeArray(),function(a,c){b[c.name]=c.value}),console.log(b),$(".newestCat").html(b.name),addCat(b),$("#add-cat-form").find("input[type=text]").val("")}function fetchCats(){$.ajax({type:"GET",url:"/cats",success:function(a){console.log(a),postCats(a)}})}function addCat(a){console.log(a),$.ajax({type:"POST",url:"/add",data:a,success:function(a){console.log(a),fetchCats()}})}function postCats(a){$(".allCats").empty();for(var b=0;b<a.length;b++){$(".allCats").append("<div></div>");var c=$(".allCats").children().last();c.append("<p>"+a[b].name+"</p>")}}$(document).ready(function(){init(),enable()});